class HuggingFaceDatasetComponent(CustomComponent):
    display_name = "HuggingFace Dataset"
    description = "Retrieve datasets from Hugging Face."

    def build_config(self):
        return {
            "dataset_name": {"display_name": "Dataset Name", "info": "Name of the dataset to retrieve."},
            "huggingface_token": {
                "display_name": "Hugging Face Token",
                "password": True,
                "info": "Token for Hugging Face API authentication (optional).",
                "required": False,
            },
        }

    def build(self, dataset_name: str, huggingface_token: Optional[str] = None) -> DatasetDict:
        return load_dataset(dataset_name, use_auth_token=huggingface_token if huggingface_token else None)

from dfapp.interface.custom.custom_component import CustomComponent
from datasets import DatasetDict


class PushToHubComponent(CustomComponent):
    display_name = "Push Dataset to HuggingFace Hub"
    description = "Pushes a dataset to the Hugging Face Hub."

    def build_config(self):
        return {
            "user_name": {"display_name": "hf user name", "info": "Hugging Face username."},
            "dataset_name": {
                "display_name": "hf dataset name",
                "info": "Name of the dataset to push. If it includes a '/', it will be used as the full repo_id.",
                "required": True
                },
            "huggingface_token": {
                "display_name": "Hugging Face Token",
                "password": True,
                "info": "Token for Hugging Face API authentication.",
                "required": True,
            },
        }

    def build(self, dataset: DatasetDict, user_name: str, dataset_name: str, huggingface_token: str):
        if '/' in dataset_name:
            repo_id = dataset_name
        else:
            repo_id = f"{user_name}/{dataset_name}"
        dataset.push_to_hub(repo_id, token=huggingface_token)
